import React, { useState, useEffect } from 'react';
import axios from 'axios';

const styles = {
  container: {
    marginBottom: '8px',
  },
  select: {
    width: '100%',
    padding: '8px 10px',
    borderRadius: '6px',
    border: '1px solid #d1d5db',
    backgroundColor: 'white',
    fontSize: '13px',
    outline: 'none',
    cursor: 'pointer',
    marginBottom: '4px',
  },
  label: {
    display: 'block',
    fontSize: '12px',
    fontWeight: '600',
    marginBottom: '4px',
    color: '#4b5563',
  },
  description: {
    fontSize: '11px',
    color: '#6b7280',
    fontStyle: 'italic',
    marginTop: '2px',
  },
  errorContainer: {
    fontSize: '12px',
    color: '#dc2626',
    backgroundColor: '#fee2e2',
    padding: '4px 8px',
    borderRadius: '4px',
    marginTop: '4px',
  }
};

function ModelSelector({ selectedModel, onModelChange }) {
  const [models, setModels] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Define a function to fetch models that we can reuse
  const fetchModelsFromAPI = async () => {
    try {
      setIsLoading(true);
      setError(null); // Reset error state on new fetch attempt
      
      console.log('Fetching AI models from http://localhost:5001/ai/models...');
      const response = await axios.get('http://localhost:5001/ai/models', {
        timeout: 10000 // 10 second timeout
      });
      
      console.log('Response received:', response.status, response.data);
      
      if (response.data && response.data.models && Array.isArray(response.data.models)) {
        console.log('Models loaded successfully:', response.data.models.length);
        
        // Always ensure we have at least a fallback model for display
        let modelsToSet = response.data.models;
        if (modelsToSet.length === 0) {
          modelsToSet = [{
            id: "gpt3.5",
            name: "GPT-3.5 Turbo (Fallback)",
            provider: "openai",
            description: "Fallback model when no others are available"
          }];
        }
        
        setModels(modelsToSet);
        
        // Handle model selection
        if (modelsToSet.length > 0) {
          // If no model is currently selected, select one
          if (!selectedModel) {
            // Prefer GPT-4o (labeled as gpt3.5 in our system) for most reliability
            const defaultId = modelsToSet.some(m => m.id === 'gpt3.5') 
              ? 'gpt3.5' 
              : modelsToSet[0].id;
            onModelChange(defaultId);
            console.log('Auto-selecting model:', defaultId);
          }
          // If current selection is not available, switch to a new one
          else if (!modelsToSet.some(m => m.id === selectedModel)) {
            const newDefaultId = modelsToSet.some(m => m.id === 'gpt3.5')
              ? 'gpt3.5'
              : modelsToSet[0].id;
            onModelChange(newDefaultId);
            console.log('Selected model not available, switching to:', newDefaultId);
          }
        }
      } else {
        console.error('Invalid response structure:', response.data);
        setError('Failed to load models (Invalid response format)');
        // Set a fallback model anyway
        setModels([{
          id: "gpt3.5",
          name: "GPT-3.5 Turbo (Fallback)",
          provider: "openai",
          description: "Fallback model when API returns invalid data"
        }]);
      }
    } catch (err) {
      console.error('Failed to fetch AI models:', err);
      
      // Set fallback model
      setModels([{
        id: "gpt3.5",
        name: "GPT-3.5 Turbo (Fallback)",
        provider: "openai",
        description: "Default model when connection errors occur"
      }]);
      
      // Provide detailed error messages based on error type
      if (err.response) {
        console.error(`Server error: ${err.response.status}`, err.response.data);
        setError(`Server error ${err.response.status}: ${err.response?.data?.message || 'Unknown server error'}`);
      } else if (err.request) {
        console.error('No response received:', err.request);
        setError('Cannot connect to server - is the backend running?');
      } else {
        console.error('Request error:', err.message);
        setError(`Request failed: ${err.message}`);
      }
      
      // If we have a model selected, keep using it
      if (!selectedModel) {
        onModelChange('gpt3.5');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Initial fetch when component mounts
  useEffect(() => {
    fetchModelsFromAPI();
  }, []);

  // Find selected model to display description
  const selectedModelInfo = models.find(model => model.id === selectedModel);
  
  // Handler for manual retry
  const handleRetryFetch = () => {
    console.log('Manually retrying model fetch...');
    fetchModelsFromAPI();
  };

  // If we have no models but have a selected model from props, use a fallback
  const displayModels = models.length > 0 ? models : selectedModel ? [{ 
    id: selectedModel, 
    name: `${selectedModel} (Default)` 
  }] : [];

  return (
    <div style={styles.container}>
      <label style={styles.label}>AI Model:</label>
      <select 
        value={selectedModel || ''}
        onChange={(e) => onModelChange(e.target.value)}
        disabled={isLoading}
        style={styles.select}
      >
        {isLoading && <option value="">Loading models...</option>}
        {!isLoading && displayModels.map(model => (
          <option key={model.id} value={model.id}>
            {model.name}
          </option>
        ))}
      </select>
      
      {selectedModelInfo && (
        <div style={styles.description}>
          {selectedModelInfo.description}
        </div>
      )}
      
      {error && (
        <div style={styles.errorContainer}>
          {error}
          <button 
            onClick={handleRetryFetch}
            style={{
              marginLeft: '8px',
              padding: '2px 6px',
              backgroundColor: '#4F46E5',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              fontSize: '11px',
              cursor: 'pointer'
            }}
          >
            Retry
          </button>
        </div>
      )}
    </div>
  );
}

export default ModelSelector;
