import React, { useState, useRef, useEffect } from 'react';
import { getAiAssistance } from '../services/aiService';

const AIAssistant = ({ latexCode }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [messages, setMessages] = useState([
    { role: 'system', content: 'Hi! I\'m your resume assistant. How can I help improve your resume?' }
  ]);
  const [prompt, setPrompt] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  
  // Auto-scroll to the latest message
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Submit handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    // Add user message to chat
    setMessages(prev => [...prev, { role: 'user', content: prompt }]);
    
    // Clear input
    setPrompt('');
    
    // Set loading state
    setIsLoading(true);
    
    try {
      // Get AI response
      const response = await getAiAssistance(prompt, latexCode);
      
      // Add AI response to chat
      setMessages(prev => [...prev, { role: 'assistant', content: response.response }]);
    } catch (error) {
      // Add error message
      setMessages(prev => [...prev, { 
        role: 'error', 
        content: 'Sorry, I couldn\'t process that request. Please try again.' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Toggle chat expansion
  const toggleExpansion = () => {
    setIsExpanded(prev => !prev);
  };

  // Predefined prompts
  const predefinedPrompts = [
    "Improve my work experience section",
    "Add more power words",
    "Suggest better technical skills format",
    "Optimize for ATS"
  ];
  
  // Handle clicking predefined prompt
  const handlePredefinedPrompt = (prompt) => {
    setPrompt(prompt);
  };

  return (
    <div className={`bg-white rounded-t-lg shadow-lg transition-all duration-300 border border-gray-200 ${isExpanded ? 'h-96' : 'h-12'}`} 
         style={{maxHeight: isExpanded ? '24rem' : '3rem'}}>
      {/* Header */}
      <div 
        className="bg-indigo-600 text-white p-3 rounded-t-lg flex items-center justify-between cursor-pointer"
        onClick={toggleExpansion}
      >
        <div className="flex items-center">
          <span className="material-icons mr-2 text-xl">smart_toy</span>
          <h3 className="font-medium">Resume AI Assistant</h3>
        </div>
        <span className="material-icons">
          {isExpanded ? 'expand_more' : 'expand_less'}
        </span>
      </div>
      
      {/* Chat content - only visible when expanded */}
      <div className={`${isExpanded ? 'flex flex-col h-full' : 'hidden'}`}>
        {/* Messages */}
        <div className="flex-1 p-4 overflow-y-auto bg-gray-50" style={{maxHeight: 'calc(24rem - 7rem)'}}>
          {messages.map((msg, index) => (
            <div 
              key={index} 
              className={`mb-3 p-2 rounded-lg max-w-[85%] ${
                msg.role === 'user' 
                  ? 'bg-blue-100 ml-auto' 
                  : msg.role === 'error'
                    ? 'bg-red-100'
                    : 'bg-white border border-gray-200'
              }`}
            >
              <p className="text-sm">{msg.content}</p>
            </div>
          ))}
          {isLoading && (
            <div className="flex items-center space-x-2 p-2">
              <div className="w-2 h-2 rounded-full bg-gray-500 animate-bounce" style={{ animationDelay: '0s' }}></div>
              <div className="w-2 h-2 rounded-full bg-gray-500 animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              <div className="w-2 h-2 rounded-full bg-gray-500 animate-bounce" style={{ animationDelay: '0.4s' }}></div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Quick prompts */}
        <div className="px-4 py-2 border-t border-gray-200 flex overflow-x-auto gap-2">
          {predefinedPrompts.map((promptText, index) => (
            <button
              key={index}
              className="px-3 py-1 bg-gray-100 text-xs rounded-full whitespace-nowrap hover:bg-gray-200 flex-shrink-0"
              onClick={() => handlePredefinedPrompt(promptText)}
            >
              {promptText}
            </button>
          ))}
        </div>
        
        {/* Input area */}
        <form onSubmit={handleSubmit} className="p-2 border-t border-gray-200 flex">
          <input
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Ask for resume advice..."
            className="flex-1 p-2 border border-gray-300 rounded-l-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isLoading}
          />
          <button
            type="submit"
            className={`px-4 py-2 bg-blue-600 text-white rounded-r-md ${
              isLoading || !prompt.trim() ? 'opacity-50 cursor-not-allowed' : 'hover:bg-blue-700'
            }`}
            disabled={isLoading || !prompt.trim()}
          >
            <span className="material-icons text-sm">send</span>
          </button>
        </form>
      </div>
    </div>
  );
};

export default AIAssistant;
