import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const styles = {
  container: {
    position: 'absolute',
    bottom: '20px',
    left: '20px',
    width: 'calc(50% - 40px)',
    maxHeight: '300px',
    backgroundColor: '#ffffff',
    borderRadius: '10px',
    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.2)',
    display: 'flex',
    flexDirection: 'column',
    overflow: 'hidden',
    zIndex: 1000,
    border: '1px solid #e5e7eb',
    transition: 'all 0.3s ease',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px 15px',
    backgroundColor: '#1f2937',
    color: 'white',
    fontWeight: 600,
  },
  minimized: {
    height: '50px',
    overflow: 'hidden',
  },
  body: {
    padding: '10px 15px',
    overflowY: 'auto',
    flex: 1,
    backgroundColor: '#f9fafb',
  },
  chatBox: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    maxHeight: '200px',
    overflowY: 'auto',
  },
  message: {
    padding: '10px',
    borderRadius: '8px',
    maxWidth: '85%',
    wordBreak: 'break-word',
  },
  userMessage: {
    backgroundColor: '#3b82f6',
    color: 'white',
    alignSelf: 'flex-end',
  },
  aiMessage: {
    backgroundColor: '#e5e7eb',
    color: '#1f2937',
    alignSelf: 'flex-start',
  },
  inputContainer: {
    display: 'flex',
    borderTop: '1px solid #e5e7eb',
    padding: '10px',
    backgroundColor: 'white',
  },
  input: {
    flex: 1,
    padding: '10px',
    border: '1px solid #d1d5db',
    borderRadius: '6px',
    fontSize: '14px',
    outline: 'none',
  },
  button: {
    padding: '0 15px',
    backgroundColor: '#3b82f6',
    color: 'white',
    borderRadius: '6px',
    marginLeft: '10px',
    border: 'none',
    cursor: 'pointer',
    fontSize: '14px',
    fontWeight: 600,
  },
  toggleButton: {
    backgroundColor: 'transparent',
    border: 'none',
    color: 'white',
    cursor: 'pointer',
    fontSize: '18px',
  },
  loadingSpinner: {
    display: 'inline-block',
    width: '20px',
    height: '20px',
    border: '2px solid rgba(255, 255, 255, 0.3)',
    borderRadius: '50%',
    borderTopColor: 'white',
    animation: 'spin 1s ease-in-out infinite',
  },
  codeBlock: {
    backgroundColor: '#1e1e1e',
    color: '#d4d4d4',
    padding: '10px',
    borderRadius: '4px',
    fontFamily: "'Fira Code', monospace",
    fontSize: '13px',
    overflowX: 'auto',
    whiteSpace: 'pre-wrap',
  },
  latexBlock: {
    backgroundColor: '#f8f8f8',
    padding: '10px',
    borderLeft: '3px solid #3b82f6',
    fontFamily: "'Fira Code', monospace",
    fontSize: '13px',
    overflowX: 'auto',
  },
};

// Function to parse markdown-like syntax for code and LaTeX blocks
const formatMessage = (message) => {
  if (!message) return '';
  
  // Replace code blocks with styled divs
  const codeBlockRegex = /```(latex)?\n([\s\S]*?)```/g;
  
  let parts = [];
  let lastIndex = 0;
  let match;
  
  while ((match = codeBlockRegex.exec(message)) !== null) {
    // Add text before the code block
    if (match.index > lastIndex) {
      parts.push({
        type: 'text',
        content: message.substring(lastIndex, match.index)
      });
    }
    
    // Add the code block
    const isLatex = match[1] === 'latex';
    parts.push({
      type: isLatex ? 'latex' : 'code',
      content: match[2]
    });
    
    lastIndex = match.index + match[0].length;
  }
  
  // Add any remaining text
  if (lastIndex < message.length) {
    parts.push({
      type: 'text',
      content: message.substring(lastIndex)
    });
  }
  
  // Render the parts
  return parts.map((part, index) => {
    if (part.type === 'text') {
      return <p key={index} style={{margin: '5px 0'}}>{part.content}</p>;
    } else if (part.type === 'latex') {
      return (
        <div key={index} style={styles.latexBlock}>
          <div style={{fontWeight: 'bold', marginBottom: '5px'}}>LaTeX:</div>
          {part.content}
        </div>
      );
    } else {
      return <div key={index} style={styles.codeBlock}>{part.content}</div>;
    }
  });
};

function AIAssistant({ latexCode, onApplyChanges }) {
  const [isMinimized, setIsMinimized] = useState(false);
  const [prompt, setPrompt] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversation, setConversation] = useState([
    { 
      role: 'ai', 
      content: 'Hello! I\'m your Resume AI Assistant. I can help you create a professional, ATS-friendly resume. Try these prompts:\n\n- "Transform my resume to highlight leadership skills"\n- "Redesign my resume for a software engineering role"\n- "Make my resume more concise and impactful"\n- "Improve the formatting of my resume"\n- "Add quantifiable achievements to my work experience"'
    }
  ]);
  const chatboxRef = useRef(null);
  
  // Auto-scroll to bottom of chat when conversation updates
  useEffect(() => {
    if (chatboxRef.current) {
      chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;
    }
  }, [conversation]);
  
  const handleSendPrompt = async () => {
    if (!prompt.trim()) return;
    
    // Add user message to conversation
    const userMessage = { role: 'user', content: prompt };
    setConversation(prev => [...prev, userMessage]);
    
    // Clear input field
    setPrompt('');
    
    // Start loading indicator
    setIsLoading(true);
    
    try {
      // Show a temporary "thinking" message
      const thinkingMessageId = Date.now();
      setConversation(prev => [
        ...prev, 
        { 
          role: 'ai', 
          content: 'Thinking...',
          id: thinkingMessageId,
          isTemporary: true
        }
      ]);
      
      // Make API call with increased timeout
      const response = await axios.post('http://localhost:5001/ai/improve-resume', {
        prompt,
        latex: latexCode
      }, { 
        timeout: 60000 // 60 second timeout
      });
      
      // Remove the temporary thinking message
      setConversation(prev => 
        prev.filter(msg => !msg.isTemporary || msg.id !== thinkingMessageId)
      );
      
      // Add AI response to conversation
      if (response.data && response.data.success) {
        setConversation(prev => [...prev, { role: 'ai', content: response.data.response }]);
      } else {
        throw new Error(response.data?.error || 'Failed to get AI response');
      }
    } catch (error) {
      console.error('Error getting AI response:', error);
      
      // Remove any temporary messages
      setConversation(prev => prev.filter(msg => !msg.isTemporary));
      
      // Provide a helpful fallback response when API fails
      const fallbackResponse = generateFallbackResponse(prompt, latexCode);
      setConversation(prev => [
        ...prev, 
        { role: 'ai', content: fallbackResponse }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  // Function to generate basic fallback responses when API fails
  const generateFallbackResponse = (userPrompt, latex) => {
    const promptLower = userPrompt.toLowerCase();
    
    if (promptLower.includes('improve') || promptLower.includes('enhance')) {
      return "I'd suggest improving your resume by quantifying achievements, using action verbs, and ensuring consistent formatting. For example:\n\n```latex\n\\section{Experience}\n\\textbf{Software Engineer} \\\\\nCompany Name, City, State \\\\\n2022-Present\n\\begin{itemize}\n  \\item Increased application performance by 40\\% through optimization of database queries\n  \\item Led development of 3 key features that attracted 1,000+ new users\n  \\item Collaborated with cross-functional teams to reduce deployment time by 25\\%\n\\end{itemize}\n```";
    } else if (promptLower.includes('format') || promptLower.includes('layout')) {
      return "For better formatting, consider using a cleaner layout:\n\n```latex\n\\documentclass[11pt,a4paper,sans]{moderncv}\n\\moderncvstyle{classic}\n\\moderncvcolor{blue}\n\n\\name{John}{Doe}\n\\phone{(123) 456-7890}\n\\email{john.doe@example.com}\n\\social[linkedin]{linkedin.com/in/johndoe}\n\n\\begin{document}\n\\makecvtitle\n\n% Rest of your resume...\n\\end{document}\n```";
    } else if (promptLower.includes('skills') || promptLower.includes('highlight')) {
      return "To highlight your skills more effectively, consider grouping them by category:\n\n```latex\n\\section{Skills}\n\\begin{itemize}\n  \\item \\textbf{Programming Languages:} JavaScript (ES6+), TypeScript, Python, Java\n  \\item \\textbf{Web Technologies:} React, Node.js, Express, HTML5, CSS3/SCSS\n  \\item \\textbf{Databases:} MongoDB, PostgreSQL, Redis\n  \\item \\textbf{DevOps:} Docker, Kubernetes, AWS, CI/CD pipelines\n  \\item \\textbf{Tools:} Git, Jira, Figma, Jest, Webpack\n\\end{itemize}\n```";
    } else {
      return "I'm having trouble connecting to the AI service right now. Here are some general resume tips:\n\n1. Keep your resume concise and focused\n2. Quantify achievements when possible\n3. Use action verbs to describe your experience\n4. Ensure consistent formatting throughout\n5. Tailor your resume for each job application\n\nYou can improve your LaTeX code structure with:\n\n```latex\n\\section{Your Section}\n\\subsection{A Subsection}\n\\begin{itemize}\n  \\item Use bullet points for clear, scannable content\n  \\item Highlight key achievements and skills\n\\end{itemize}\n```";
    }
  };

  // Function to validate LaTeX code before applying changes
  const validateLatexCode = (code) => {
    // Basic validation check - make sure it has the essential document structure
    const hasDocumentClass = code.includes('\\documentclass');
    const hasBeginDocument = code.includes('\\begin{document}');
    const hasEndDocument = code.includes('\\end{document}');
    
    return hasDocumentClass && hasBeginDocument && hasEndDocument;
  };

  const handleApplyChanges = (latexContent) => {
    if (onApplyChanges && latexContent) {
      // Extract LaTeX code from message
      const latexMatch = latexContent.match(/```latex\n([\s\S]*?)```/);
      if (latexMatch && latexMatch[1]) {
        const extractedLatex = latexMatch[1];
        
        // Validate the LaTeX code before applying
        if (validateLatexCode(extractedLatex)) {
          onApplyChanges(extractedLatex);
        } else {
          // Show an error in the conversation if the code is invalid
          setConversation(prev => [
            ...prev,
            {
              role: 'ai',
              content: "⚠️ The LaTeX code appears to be incomplete. A valid LaTeX document should include \\documentclass, \\begin{document}, and \\end{document}. Please ask for a complete document."
            }
          ]);
        }
      }
    }
  };

  // Enhanced suggestion prompts based on user input
  const getSuggestions = (inputText) => {
    inputText = inputText.toLowerCase();
    
    if (inputText.includes('design') || inputText.includes('template') || inputText.includes('format')) {
      return [
        "Create a modern, clean layout for my resume",
        "Format my resume for a technical role",
        "Design an executive-level resume layout",
        "Improve the section spacing and organization"
      ];
    }
    
    if (inputText.includes('experience') || inputText.includes('job')) {
      return [
        "Strengthen my work experience section with achievements",
        "Add metrics and results to my job descriptions",
        "Rewrite my experience to focus on leadership",
        "Optimize my job history for ATS systems"
      ];
    }
    
    if (inputText.includes('skill') || inputText.includes('technology')) {
      return [
        "Organize my skills into meaningful categories",
        "Highlight my most relevant technical skills",
        "Add a skills rating system",
        "Create a more scannable skills section"
      ];
    }
    
    return [
      "Improve my resume's overall impact and readability",
      "Tailor my resume for a specific job posting",
      "Add professional summary section",
      "Create a more achievement-oriented resume"
    ];
  };
  
  // Function to show suggestions as user types
  const [suggestions, setSuggestions] = useState([]);
  
  useEffect(() => {
    if (prompt.length > 3) {
      setSuggestions(getSuggestions(prompt));
    } else {
      setSuggestions([]);
    }
  }, [prompt]);
  
  const handleSuggestionClick = (suggestion) => {
    setPrompt(suggestion);
    setSuggestions([]);
  };
  
  return (
    <div style={{...styles.container, ...(isMinimized ? styles.minimized : {})}}>
      <div style={styles.header}>
        <div>Resume AI Assistant</div>
        <button
          style={styles.toggleButton}
          onClick={() => setIsMinimized(!isMinimized)}
        >
          {isMinimized ? '▲' : '▼'}
        </button>
      </div>
      
      {!isMinimized && (
        <>
          <div style={styles.body}>
            <div style={styles.chatBox} ref={chatboxRef}>
              {conversation.map((msg, index) => (
                <div
                  key={index}
                  style={{
                    ...styles.message,
                    ...(msg.role === 'user' ? styles.userMessage : styles.aiMessage)
                  }}
                >
                  {msg.role === 'ai' ? (
                    <div>
                      {formatMessage(msg.content)}
                      {msg.content.includes('```latex') && (
                        <button
                          onClick={() => handleApplyChanges(msg.content)}
                          style={{
                            marginTop: '10px',
                            padding: '5px 10px',
                            backgroundColor: '#22c55e',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer',
                          }}
                        >
                          Apply Changes
                        </button>
                      )}
                    </div>
                  ) : (
                    msg.content
                  )}
                </div>
              ))}
            </div>
          </div>
          
          <div style={styles.inputContainer}>
            <div style={{ position: 'relative', flex: 1 }}>
              <input
                type="text"
                placeholder="Ask about improving your resume..."
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendPrompt()}
                style={styles.input}
                disabled={isLoading}
              />
              {suggestions.length > 0 && (
                <div style={{
                  position: 'absolute',
                  top: '-160px',
                  left: 0,
                  right: 0,
                  backgroundColor: 'white',
                  border: '1px solid #d1d5db',
                  borderRadius: '6px',
                  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                  zIndex: 10
                }}>
                  {suggestions.map((suggestion, index) => (
                    <div
                      key={index}
                      onClick={() => handleSuggestionClick(suggestion)}
                      style={{
                        padding: '10px 15px',
                        borderBottom: index < suggestions.length - 1 ? '1px solid #e5e7eb' : 'none',
                        cursor: 'pointer',
                        fontSize: '13px',
                        color: '#374151'
                      }}
                      onMouseOver={(e) => e.target.style.backgroundColor = '#f9fafb'}
                      onMouseOut={(e) => e.target.style.backgroundColor = 'transparent'}
                    >
                      {suggestion}
                    </div>
                  ))}
                </div>
              )}
            </div>
            <button
              onClick={handleSendPrompt}
              style={styles.button}
              disabled={isLoading}
            >
              {isLoading ? (
                <div style={styles.loadingSpinner}></div>
              ) : (
                'Send'
              )}
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default AIAssistant;
