import React, { useState, useEffect } from 'react';
import axios from 'axios';

const styles = {
  container: {
    marginBottom: '8px',
  },
  select: {
    width: '100%',
    padding: '8px 10px',
    borderRadius: '6px',
    border: '1px solid #d1d5db',
    backgroundColor: 'white',
    fontSize: '13px',
    outline: 'none',
    cursor: 'pointer',
    marginBottom: '4px',
  },
  label: {
    display: 'block',
    fontSize: '12px',
    fontWeight: '600',
    marginBottom: '4px',
    color: '#4b5563',
  },
  description: {
    fontSize: '11px',
    color: '#6b7280',
    fontStyle: 'italic',
    marginTop: '2px',
  }
};

function ModelSelector({ selectedModel, onModelChange }) {
  const [models, setModels] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchModels = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get('http://localhost:5001/ai/models');
        if (response.data && response.data.models) {
          setModels(response.data.models);
          
          // Set default model if none selected and models are available
          if (!selectedModel && response.data.models.length > 0) {
            onModelChange(response.data.models[0].id);
          }
        }
      } catch (err) {
        console.error('Failed to fetch AI models:', err);
        setError('Failed to load AI models');
      } finally {
        setIsLoading(false);
      }
    };

    fetchModels();
  }, [selectedModel, onModelChange]);

  // Find the selected model to display its description
  const selectedModelInfo = models.find(model => model.id === selectedModel);

  return (
    <div style={styles.container}>
      <label style={styles.label}>AI Model:</label>
      <select 
        value={selectedModel || ''}
        onChange={(e) => onModelChange(e.target.value)}
        disabled={isLoading}
        style={styles.select}
      >
        {isLoading && <option value="">Loading models...</option>}
        {error && <option value="">Error loading models</option>}
        {!isLoading && !error && models.length === 0 && <option value="">No models available</option>}
        
        {models.map(model => (
          <option key={model.id} value={model.id}>
            {model.name}
          </option>
        ))}
      </select>
      
      {selectedModelInfo && (
        <div style={styles.description}>
          {selectedModelInfo.description}
        </div>
      )}
    </div>
  );
}

export default ModelSelector;
