import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import ModelSelector from './ModelSelector';
import '../styles/AIAssistant.css';

function AIAssistant({ latexCode, onApplyChanges, darkMode }) {
  const [isMinimized, setIsMinimized] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [prompt, setPrompt] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedModel, setSelectedModel] = useState('deepseek-chat');
  const [conversation, setConversation] = useState([
    {
      role: 'ai',
      content: 'Hello! I can help improve your resume using advanced AI. Currently using DeepSeek Chat. What would you like me to help with?'
    }
  ]);
  
  const chatBodyRef = useRef(null);
  const inputRef = useRef(null);
  
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
    
    // Focus input when assistant is opened
    if (!isMinimized && !isCollapsed && inputRef.current) {
      inputRef.current.focus();
    }
  }, [conversation, isMinimized, isCollapsed]);
  
  const handleSendPrompt = async () => {
    if (!prompt.trim() || isLoading) return;
    
    // Add user message to conversation
    const userMessage = { role: 'user', content: prompt };
    setConversation(prev => [...prev, userMessage]);
    
    // Clear input field
    setPrompt('');
    
    // Start loading
    setIsLoading(true);
    
    // Save the prompt for potential retry
    const currentPrompt = prompt;
    
    try {
      // Add temporary "thinking" message
      setConversation(prev => [
        ...prev, 
        { role: 'ai', content: `Thinking... (using ${selectedModel})`, isTemporary: true }
      ]);
      
      // Call the backend API with increased timeout
      const response = await axios.post('http://localhost:5001/ai/improve-resume', {
        prompt: currentPrompt,
        latex: latexCode,
        modelId: selectedModel
      }, { 
        timeout: 180000 // 3 minutes timeout - increased for slower connections
      });
      
      // Remove temporary message
      setConversation(prev => prev.filter(msg => !msg.isTemporary));
      
      if (response.data && response.data.success) {
        setConversation(prev => [...prev, { role: 'ai', content: response.data.response }]);
      } else {
        throw new Error(response.data?.error || 'Failed to get response from AI');
      }
    } catch (error) {
      console.error('Error getting AI response:', error);
      
      // Remove temporary message
      setConversation(prev => prev.filter(msg => !msg.isTemporary));
      
      // Add error message with more helpful information
      let errorMsg = "I'm having trouble connecting to the AI service. ";
      
      if (error.response) {
        errorMsg += `Server error: ${error.response.status}`;
        if (error.response.status === 400) {
          errorMsg += " - Bad request. This is often due to an API configuration issue.";
        } else if (error.response.status === 401) {
          errorMsg += " - Authentication failed. API key may be invalid.";
        } else if (error.response.status === 429) {
          errorMsg += " - Too many requests. The API rate limit has been exceeded.";
        } else if (error.response.status >= 500) {
          errorMsg += " - The AI service is currently unavailable. Please try again later.";
        }
      } else if (error.code === 'ECONNABORTED') {
        errorMsg += "The request timed out. This could be due to high server load or network issues.";
      } else if (error.message.includes('Network Error')) {
        errorMsg += "Network error. Please check your internet connection.";
      } else {
        errorMsg += error.message;
      }
      
      errorMsg += "\n\nTry these steps:\n1. Choose a different AI model from the dropdown\n2. Refresh the page and try again\n3. Check if the server is running";
      
      setConversation(prev => [
        ...prev, 
        { role: 'ai', content: errorMsg }
      ]);
    } finally {
      setIsLoading(false);
    }
  };
  
  // Extract and apply LaTeX code
  const handleApplyChanges = (content) => {
    const latexMatch = content.match(/```latex\n([\s\S]*?)```/);
    if (latexMatch && latexMatch[1]) {
      onApplyChanges(latexMatch[1]);
    }
  };
  
  // Format message content - handle LaTeX code blocks
  const formatMessage = (content) => {
    if (!content) return '';
    
    if (content.startsWith('Thinking...')) {
      return (
        <div className="thinking-indicator">
          <div className="typing-indicator">
            <span></span>
            <span></span>
            <span></span>
          </div>
          <span className="thinking-text">{content}</span>
        </div>
      );
    }
    
    // Split by code blocks
    const parts = [];
    let lastIndex = 0;
    
    // Match ```latex ... ``` blocks
    const codeBlockRegex = /```latex\n([\s\S]*?)```/g;
    let match;
    
    while ((match = codeBlockRegex.exec(content)) !== null) {
      // Add text before code block
      if (match.index > lastIndex) {
        parts.push({
          type: 'text',
          content: content.substring(lastIndex, match.index)
        });
      }
      
      // Add the code block
      parts.push({
        type: 'latex',
        content: match[1]
      });
      
      lastIndex = match.index + match[0].length;
    }
    
    // Add any remaining text
    if (lastIndex < content.length) {
      parts.push({
        type: 'text',
        content: content.substring(lastIndex)
      });
    }
    
    // Render parts
    return parts.map((part, index) => {
      if (part.type === 'text') {
        // Format markdown-style syntax in text parts
        const formattedText = part.content
          .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
          .replace(/\*(.*?)\*/g, '<em>$1</em>')
          .replace(/`(.*?)`/g, '<code>$1</code>');
          
        return (
          <div 
            key={index} 
            className="text-content"
            dangerouslySetInnerHTML={{ __html: formattedText }}
          />
        );
      } else {
        return (
          <div key={index} className="latex-block-container">
            <pre className="latex-block">
              <div className="latex-header">
                <span>LaTeX Code</span>
              </div>
              <code>{part.content}</code>
            </pre>
            <button 
              className="apply-btn"
              onClick={() => handleApplyChanges('```latex\n' + part.content + '```')}
            >
              <i className="fa fa-check-circle"></i> Apply Changes
            </button>
          </div>
        );
      }
    });
  };
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendPrompt();
    }
  };
  
  const handleMaximize = () => {
    setIsMinimized(false);
    setIsCollapsed(false);
  };
  
  const handleMinimize = () => {
    setIsMinimized(true);
    setIsCollapsed(false);
  };
  
  const handleCollapse = () => {
    setIsCollapsed(!isCollapsed);
    setIsMinimized(false);
  };

  return (
    <div className={`ai-assistant ${darkMode ? 'dark-mode' : ''} ${isMinimized ? 'minimized' : ''} ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="assistant-header">
        <div className="assistant-title">
          <i className="fa fa-robot"></i>
          <span>AI Assistant</span>
        </div>
        
        <div className="assistant-controls">
          <button 
            className="assistant-control"
            onClick={handleMinimize}
            title="Minimize"
          >
            <i className="fa fa-window-minimize"></i>
          </button>
          
          <button 
            className="assistant-control"
            onClick={handleCollapse}
            title={isCollapsed ? "Expand" : "Collapse"}
          >
            <i className={`fa fa-${isCollapsed ? 'expand' : 'compress'}`}></i>
          </button>
          
          <button 
            className="assistant-control close"
            onClick={() => setIsMinimized(true)}
            title="Close"
          >
            <i className="fa fa-times"></i>
          </button>
        </div>
      </div>
      
      {!isMinimized && (
        <>
          {!isCollapsed && (
            <div className="model-selector-container">
              <ModelSelector 
                selectedModel={selectedModel}
                onModelChange={setSelectedModel}
                darkMode={darkMode}
              />
            </div>
          )}
          
          <div className="assistant-body" ref={chatBodyRef}>
            <div className="messages-container">
              {conversation.map((msg, index) => (
                <div
                  key={index}
                  className={`message ${msg.role}`}
                >
                  <div className="message-avatar">
                    {msg.role === 'ai' ? (
                      <i className="fa fa-robot"></i>
                    ) : (
                      <i className="fa fa-user"></i>
                    )}
                  </div>
                  <div className="message-content">
                    {msg.role === 'ai' ? formatMessage(msg.content) : msg.content}
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          <div className="assistant-input">
            <textarea
              ref={inputRef}
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask how to improve your resume..."
              disabled={isLoading}
              rows={1}
            />
            <button
              className={`send-button ${isLoading || !prompt.trim() ? 'disabled' : ''}`}
              onClick={handleSendPrompt}
              disabled={isLoading || !prompt.trim()}
            >
              {isLoading ? (
                <div className="button-spinner"></div>
              ) : (
                <i className="fa fa-paper-plane"></i>
              )}
            </button>
          </div>
        </>
      )}
      
      {isMinimized && (
        <div className="minimized-indicator" onClick={handleMaximize}>
          <i className="fa fa-robot"></i>
          <span>Open AI Assistant</span>
        </div>
      )}
    </div>
  );
}

export default AIAssistant;
