import axios from 'axios';

const API_URL = 'http://localhost:5001';

export const getAiAssistance = async (prompt, resumeContent) => {
  try {
    console.log('Sending AI request with prompt:', prompt);
    const response = await axios.post(`${API_URL}/ai/resume-assistant`, { 
      prompt, 
      resumeContent 
    });
    console.log('AI response:', response.data);
    return response.data;
  } catch (error) {
    console.error('AI assistance error:', error.response?.data || error.message);
    throw error;
  }
};
